import React, { useState } from 'react';
import styled from 'styled-components';

export default function SubmitTabs({ chosenTab: chosen, setChosenTab: setChosen, setting }) {
  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
      <Tab text={'קודאק'} icon={DuckSvg} chosen={chosen} setChosen={setChosen} />
      {setting?.showReview && <Tab text={'משוב'} icon={ReviewSvg} chosen={chosen} setChosen={setChosen} />}
      {setting?.showTests && <Tab text={'טסטים'} icon={TestsSvg} chosen={chosen} setChosen={setChosen} />}
      <Tab text={'תרגול'} icon={PracticeSvg} chosen={chosen} setChosen={setChosen} />
    </div>
  );
}

const Tab = ({ text, icon: Icon, chosen, setChosen }) => {
  const isTabChosen = chosen === text;
  return (
    <StyledTab onClick={() => setChosen(text)}>
      {isTabChosen ? <ChosenTabSvg /> : <NonChosenTabSvg />}
      <TabContent style={{ marginTop: '10px' }}>
        <Icon color={isTabChosen ? 'white' : 'rgba(44, 36, 77, 1)'} />
        <span style={{ color: isTabChosen ? 'white' : 'rgba(44, 36, 77, 1)' }}>{text}</span>
      </TabContent>
    </StyledTab>
  );
};

const StyledTab = styled.div`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
`;

const TabContent = styled.div`
  position: absolute;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: white;
  font-size: 12px;

  svg {
    margin-bottom: 5px;
  }
`;

